do
  echo "--> $org"
  curl -u username:$OAUTHTOKEN "https://api.github.com/orgs/$org" > $targetFolder/$org.json
done;

counter=0

cd scripts



targetCSV="../data/csv/organizations.csv"
fields="login,id,url,blog,location,email,name,company,repos_url,events_url,members_url,public_members_url,avatar_url,description,public_repos,public_gists,followers,following,html_url,created_at,updated_at,type,email_pattern"

mv $targetCSV $targetCSV.backup

for jsonFile in ../$targetFolder/*.json;
  do
    echo "processing $jsonFile"
    if [[ "$counter" -eq 0 ]]; then
      cat $jsonFile | coffee json_to_csv.coffee --fields="$fields" --header=only > $targetCSV
    fi
    cat $jsonFile | coffee json_to_csv.coffee --fields="$fields" --header="false" >> $targetCSV

    counter=$((counter+1))
  done;
